<?php header('Content-type: image/png');
    require('./lib/inc/Servers/Status.php');

    $image = ImageCreateFrompng('data:;base64,');
    $white = ImageColorAllocate($image, 255, 255, 255);
    $green = ImageColorAllocate($image, 0, 204, 0);
    $red   = ImageColorAllocate($image, 255, 0, 0);

    $font = './images/font.ttf';
    if(isset($_GET['type']) && isset($_GET['port']) && isset($_GET['ip'])) {
        $st = new \inc\Servers\Status();
        $status = $st->checkStatus(1, $_GET['ip'], $_GET['port']);
        
        if($status) {
            Imagettftext($image, 10, 0, 10, 18, $green, $font, 'Hostname:');
            Imagettftext($image, 10, 0, 85, 18, $white, $font, $status['name']);

            Imagettftext($image, 10, 0, 10, 33, $green, $font, 'IP:');
            Imagettftext($image, 10, 0, 32, 33, $white, $font, $_GET['ip'] . ':' . $_GET['port']);

            Imagettftext($image, 10, 0, 10, 48, $green, $font, 'Players:');
            Imagettftext($image, 10, 0, 70, 48, $white, $font, $status['players'] . '/' . $status['maxPlayers']);

            Imagettftext($image, 10, 0, 205, 33, $green, $font, 'Mode:');
            Imagettftext($image, 10, 0, 250, 33, $white, $font, $status['mode']);

            Imagettftext($image, 10, 0, 205, 48, $green, $font, 'Map:');
            Imagettftext($image, 10, 0, 240, 48, $white, $font, $status['map']);
        } else {
            Imagettftext($image, 10, 0, 32, 35, $green, $font, 'IP:');
            Imagettftext($image, 12, 0, 53, 35, $white, $font, $_GET['ip'] . ':' . $_GET['port']);
            Imagettftext($image, 10, 4, 153, 47, $red, $font, 'Offline');
        }
    } else {
        Imagettftext($image, 30, 4, 100, 53, $red, $font, 'Wrong data!');
    }
    
    imagepng($image);
    imagedestroy($image);